import UIKit

protocol VideoPlayerBusinessLogic {
    func playerVideoRequest(request: VideoPlayer.FetchVideo.Request)
    
}

protocol VideoPlayerDataStore {
    //var name: String { get set }
    
}

class VideoPlayerInteractor: VideoPlayerBusinessLogic, VideoPlayerDataStore {
    
    var presenter: VideoPlayerPresentationLogic?
    var worker: VideoPlayerWorker?
    
    func playerVideoRequest(request: VideoPlayer.FetchVideo.Request) {
        
        worker = VideoPlayerWorker()
        worker?.doSomeWork()
        
        let url = VideoUrls.videoUrl
        
        let response = VideoPlayer.FetchVideo.Response(videoUrl: url)
        
        presenter?.presentVideoPlayer(response: response)
        
    }
    
}
