import UIKit

protocol VideoPlayerPresentationLogic {
 
    func presentVideoPlayer(response: VideoPlayer.FetchVideo.Response)
}

class VideoPlayerPresenter: VideoPlayerPresentationLogic {
    
    weak var viewController: VideoPlayerDisplayLogic?
    
    // MARK: Do something
    func presentVideoPlayer(response: VideoPlayer.FetchVideo.Response) {
        let displayedViewModel = VideoPlayer.FetchVideo.ViewModel.displayVideoPlayerViewModel(showVideoUrl: response.videoUrl)
        
        let viewModel = VideoPlayer.FetchVideo.ViewModel(displayedViewModel: displayedViewModel)
        
        viewController?.displayVideo(viewModel: viewModel)
    }

    
}
